INSERT INTO articles (title, shortDescription, description, eventDate, categoryId, imageURL, imageDescription, isMemberOnly, tags)
VALUES (
"La plateforme pour une autre pac se remobilise",
"Réforme de la Politique Agricole Commune",
"Les débats autour de la réforme de la Politique Agricole Commune se relancent ? La plateforme « Pour une autre PAC » aussi ! La présidence sera assurée pour 2018 par Réseau CIVAM...\n\nCréée en 2009 sous le nom du Groupe PAC 2013, la plateforme Pour une autre PAC a pour objet de peser sur les discussions entourant la réforme de la Politique Agricole Commune (PAC).\n\nLa plateforme se remobilise aujourd’hui, alors que les débats autour de la réforme de la PAC post 2020 sont ouverts. Pour une autre PAC défend une réforme complète de l’actuelle politique agricole commune et poursuit son combat pour une PAC plus juste, plus durable et plus cohérente.\n\nEn 2018, la présidence de la plateforme sera assurée pour an par Quentin Delachapelle, président de réseau CIVAM.",
"",
2,
"https://images.unsplash.com/photo-1512907858294-736ec4aa4bba?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=039feedd14b9ddf2ec161dd984d575c2&dpr=1&auto=format&fit=crop&w=1000&q=80&cs=tinysrgb",
"Logo pour une autre PAC",
1,
"pac,agro"
);

INSERT INTO articles (title, shortDescription, description, eventDate, categoryId, imageURL, imageDescription, isMemberOnly, tags)
VALUES (
"Cohabitation des modèles de production agricole, bilan des journées nationales",
"La cohabitation des modèles de production agricole est-elle souhaitable ?",
"<div class='item-page'><p style='text-align: justify;'>Lors de ces Journées nationales, des visites, rencontres, ateliers, interventions extérieures et débats ont amené les participants à échanger sur la cohabitation des modèles de production agricole. Retrouvez le document de synthèse des réflexions et témoignages issus de ces journées.</p><p style='text-align: justify;'>&nbsp;</p><p style='text-align: justify;'>Du 14 au 16 novembre 2017, près de 200 personnes se sont réunies dans le Finistère, à Logonna-Daoulas au Centre Moulin-Mer, à l’appel de Réseau Civam et du Civam 29.</p><p style='text-align: justify;'>&nbsp;</p><p style='text-align: justify;'>La cohabitation des modèles de production agricole est-elle souhaitable ? Un système de production agricole est-il encore légitime ? Les systèmes de production et d'alimentation durables doivent-ils changer d'échelle ? Autant de questions qui ont été débattues à cette occasion.</p><p style='text-align: justify;'>&nbsp;</p><h4 style='text-align: center;'><span style='color: #ff6600;'><strong>Pour en savoir plus, découvrez le document de synthèse <a href='http://www.civam.org/images/actus/CIVAM-JN-OK.PDF'>ICI</a></strong></span></h4><p style='text-align: center;'>&nbsp;</p><p style='text-align: center;'><a href='/images/actus/CIVAM-JN-OK.PDF'><span style='color: #ff6600;'><strong><img src='http://www.civam.org/images/publiJN.png' alt='publiJN' width='355' height='501'></strong></span></a></p><p style='text-align: justify;'>&nbsp;</p><p style='text-align: center;'><strong>Retrouvez aussi les paroles d'adhérents sur le sujet ci-dessous :</strong></p><p>&nbsp;</p><p style='text-align: center;'><iframe src='http://www.youtube.com/embed/6Z2PeQpVshU?rel=0&amp;wmode=opaque' width='425' height='350' allowfullscreen='allowfullscreen'></iframe></p></div>",
"",
3,
"https://images.unsplash.com/photo-1425934398893-310a009a77f9?ixlib=rb-0.3.5&ixid=eyJhcHBfaWQiOjEyMDd9&s=330381a5d64e86a159fb2abb5ec3da10&dpr=1&auto=format&fit=crop&w=1000&q=80&cs=tinysrgb",
"Logo pour une autre PAC",
1,
"pac"
);

